import React from 'react';
import { useParams } from 'react-router-dom';
// import {data} from './data';
import { useState } from 'react';


export const User = ({data, list}:any) =>{
    const {id} = useParams();

    console.log(data)
    const userObj = data.find((data:any) => data.name === id)

    const [user, setUser] = useState(userObj)

    const changeAge = () =>{
        setUser(user+1)
    }


    return(
        <div>

            {
                user ? 
                <div>
                    Name: {user.name}
                    Age: {user.age}
                    city: {user.city}
                    <button onClick={changeAge}>Add</button>
                </div>
                 : null
            }

        </div>

    )
}
